participant.prepare:
import org.jpos.iso.*;
import org.jpos.transaction.*;

msg = context.get("REQUEST");
source = context.get("SOURCE");
ISOMsg res = (ISOMsg) msg.clone();
res.setResponseMTI();

res.set(39, "00");

context.put("RESPONSE", res);
source.send(res);

result = TransactionConstants.PREPARED | TransactionConstants.READONLY;
